    private String directions;
    private Logger logger;

    public Parser(String directions) {
        this.directions = directions;
        this.logger = Logger.getLogger(getClass().getName());
    }

    public int parse() {
        int floors = 0;
        for (int i = 0; i < this.directions.length(); i++) {
            if (this.directions.charAt(i) == '(') {
                floors++;
            } else {
                floors--;
            }
        }
        return floors;
    }

    public int findBasement() {
        int floors = 0;
        for (int i = 0; i < this.directions.length(); i++) {
            if (this.directions.charAt(i) == '(') {
                floors++;
            } else {
                floors--;
            }
            if (floors < 0) {
                return i + 1;
            }
        }
        return -1;
    }

    public static void main(String[] args) {
        Parser parser = new Parser(DATA);
        parser.logger.log(Level.INFO, "Floor result: {0}", parser.parse());
        parser.logger.log(Level.INFO, "Basement is entered at index: {0}", parser.findBasement());
    }
